import { map } from 'rxjs/operators';
export function existingUsernameValidator(userService) {
  return control => {
    console.log('in validator: ' + control.value);
    return userService.findUsername(control.value).pipe(map(user => {
      // tslint:disable-next-line:triple-equals
      if (user != false) {
        return {
          usernameExists: true
        };
      } else {
        return null;
      }
    }));
  };
}
export function existingEmailValidator(userService) {
  return control => {
    console.log('in email validator: ' + control.value);
    return userService.findEmail(control.value).pipe(map(customer => {
      // tslint:disable-next-line:triple-equals
      if (customer != false) {
        return {
          emailExists: true
        };
      } else {
        return null;
      }
    },
    // tslint:disable-next-line:no-shadowed-variable
    error => {
      console.log(error);
    }));
  };
}
